import React,{useState, useEffect} from 'react';
import Link from 'next/link';
import Head from "next/head";
import Layout from "../components/Layout";

const Movies = ({Movies}) => {
    // state
    const [movie, setmovie] = useState([])
    const [searchQuery, setSearchQuery] = useState('star wars')
    const [loading, setLoading] = useState(false)

  return (
    <Layout title="Movies">
      <div>
        <h2>List of Movies</h2>
      </div>
    </Layout>
  )
}

Movies.getInitialProps = async () => {
  let data;
  const searchQuery = 'star wars';
  const searchUrl = `https://api.themoviedb.org/3/search/movie?query=${searchQuery}&api_key=224502cedb2aea2828098f3724fd0b0c`;

  fetch(searchUrl)
  .then(res => res.json())
  .then(data => {
    // setmovie(data.results)
    // setLoading(false)
    console.log(data.results)
  })
  .catch(err => console.log(err))

  return {
    data
  };
}

export default Movies;
